package year17.month01;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.util.UUID;

import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

/**
 * base64转化图片
 * @author: caihq 
 * @since: 2017年1月3日 上午9:33:31 
 * @history:
 */
public class Day0302 {
    private static String IN_IMAGE_URL = "d:/aaa.jpg";

    private static String OUT_IMAGE_URL = "e:/";

    public static void main(String[] args) {
        String base64Img = getImgStr();
        // System.out.println(base64Img);

        // generateImg(base64Img2);
        // String suffix = base64Img2.substring(
        // base64Img2.indexOf("data:image/") + 11,
        // base64Img2.indexOf(";base64,"));
        System.out.println(base64Img2.length());
        generateImg(base64Img2);
        System.out.println("down!");
    }

    public static String getImgStr() {
        File imgFile = new File(IN_IMAGE_URL);
        InputStream is = null;
        byte[] data = null;
        try {
            is = new FileInputStream(imgFile);
            data = new byte[is.available()];
            is.read(data);
            is.close();
        } catch (FileNotFoundException e) {
            e.printStackTrace();
        } catch (IOException e) {
            e.printStackTrace();
        }
        BASE64Encoder encode = new BASE64Encoder();
        return encode.encode(data);
    }

    public static void generateImg(String base64Img) {
        String suffix = "jpg";
        String ibase64Img = "";
        if (base64Img.indexOf("data:image/") > -1) {
            ibase64Img = base64Img.replaceFirst("data:image/\\w{1,5};base64,",
                "");
            suffix = base64Img.substring(base64Img.indexOf("data:image/") + 11,
                base64Img.indexOf(";base64,"));
        } else {
            ibase64Img = base64Img;
        }
        OutputStream out = null;
        try {
            out = new FileOutputStream(OUT_IMAGE_URL
                    + System.getProperty("file.separator")
                    + UUID.randomUUID().toString().replace("-", "") + "."
                    + suffix);
            BASE64Decoder decoder = new BASE64Decoder();
            byte[] data = decoder.decodeBuffer(ibase64Img);
            for (int i = 0; i < data.length; i++) {
                if (data[i] < 0)
                    data[i] += 256;
            }
            out.write(data);
            out.flush();
            out.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private static String base64Img2 = "";
}
